import { Subject } from 'rxjs';
export class UserModelService {
  constructor() {
    this.userModelSubject = new Subject();
    this.users = [{
      firstName: 'joe',
      lastName: 'leboss',
      email: 'baba@joe.leboss',
      drinkPreference: 'cidre',
      hobbies: ['lecture', 'jeux videos']
    }];
  }
  /**
   * Couche d'abstraction entre données recues et traitement dans l'appli. Emet une copie des données UserModel.
   */


  emitUserModel() {
    this.userModelSubject.next(this.users.slice());
  }
  /**
   * Ajoute un utilisateur du model User dans la liste des userModel
   *
   * @param userModel
   */


  addUserModel(userModel) {
    this.users.push(userModel);
    this.emitUserModel();
  }

}
